async def start(update: Update, context: CallbackContext):
    keyboard = [["‚ú® –°–¥–µ–ª–∞—Ç—å —Ä–∞—Å–∫–ª–∞–¥"]]
    markup = ReplyKeyboardMarkup(keyboard, resize_keyboard=True)
    await update.message.reply_text(
        "üîÆ –î–æ–±—Ä–æ –ø–æ–∂–∞–ª–æ–≤–∞—Ç—å –≤ *VOX OF TAROT*! –ù–∞–∂–º–∏—Ç–µ –∫–Ω–æ–ø–∫—É –Ω–∏–∂–µ, —á—Ç–æ–±—ã –ø–æ–ª—É—á–∏—Ç—å –ø—Ä–µ–¥—Å–∫–∞–∑–∞–Ω–∏–µ.", 
        reply_markup=markup,
        parse_mode="Markdown"
    )

# –û–±—Ä–∞–±–æ—Ç—á–∏–∫ –∫–Ω–æ–ø–∫–∏
async def handle_message(update: Update, context: CallbackContext):
    if update.message.text == "‚ú® –°–¥–µ–ª–∞—Ç—å —Ä–∞—Å–∫–ª–∞–¥":
        image_name = random.choice(list(MESSAGES.keys()))
        image_path = os.path.join(IMAGES_PATH, image_name)
        
        # –û—Ç–ø—Ä–∞–≤–ª—è–µ–º –∫–∞—Ä—Ç—É
        with open(image_path, "rb") as photo:
            await update.message.reply_photo(photo=photo)
        # –û—Ç–ø—Ä–∞–≤–ª—è–µ–º —Ç–µ–∫—Å—Ç
        await update.message.reply_text(MESSAGES[image_name])

# –ó–∞–ø—É—Å–∫ –±–æ—Ç–∞
if __name__ == "__main__":
    app = ApplicationBuilder().token(TOKEN).build()
    app.add_handler(CommandHandler("start", start))
    app.add_handler(MessageHandler(filters.TEXT & ~filters.COMMAND, handle_message))
    
    PORT = int(os.environ.get("PORT", 10000))
    DOMAIN = "vox-tarot-bot.onrender.com"  # –≤–∞—à –¥–æ–º–µ–Ω –Ω–∞ Render
    
    app.run_webhook(
        listen="0.0.0.0",
        port=PORT,
        webhook_url=f"https://{DOMAIN}/{TOKEN}",
        url_path=TOKEN,
        cert=None
    )
    